mapper.update:
update branch
set branch_name = #{name,jdbcType=VARCHAR},
  branch_address = #{address,jdbcType=VARCHAR},
  branch_phone = #{phone,jdbcType=CHAR}
where branch_id = #{id,jdbcType=CHAR}

mapper.select:
select branch_id, branch_name, branch_address, branch_phone
from branch
where branch_id = #{id,jdbcType=CHAR}
