mapper.update:
update ebank_user


    ebank_user_password = #{password,jdbcType=VARCHAR},


    ebank_user_state = #{state,jdbcType=TINYINT}


where ebank_user_id = #{id,jdbcType=VARCHAR}

mapper.select:
select ebank_user_id, ebank_user_password, ebank_user_state
from ebank_user
where ebank_user_id = #{id,jdbcType=VARCHAR}
