mapper.insert:
insert into subject (subject_serial, subject_name, subject_load_relationship
  )
values (#{serial,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{loadRelationship,jdbcType=TINYINT}
  )

mapper.update:
update subject
set subject_name = #{name,jdbcType=VARCHAR},
  subject_load_relationship = #{loadRelationship,jdbcType=TINYINT}
where subject_serial = #{serial,jdbcType=VARCHAR}

mapper.select:
select subject_serial, subject_name, subject_load_relationship
from subject
where subject_serial = #{serial,jdbcType=VARCHAR}
