mapper.insert:
insert into user (user_id, user_name, user_phone, 
  user_address, user_email, user_ebank_state, 
  user_mobile_bank_state, user_foreign_exchange_state
  )
values (#{id,jdbcType=CHAR}, #{name,jdbcType=VARCHAR}, #{phone,jdbcType=CHAR}, 
  #{address,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR}, #{ebankState,jdbcType=TINYINT}, 
  #{mobileBankState,jdbcType=TINYINT}, #{foreignExchangeState,jdbcType=TINYINT}
  )

mapper.select:
select user_id, user_name, user_phone, user_address, user_email, user_ebank_state, user_mobile_bank_state, user_foreign_exchange_state
from user


        user_id = #{user.id}


        and  user_phone = #{user.phone}


      and user_name = #{user.name}
